package calculator;

public class Calculator {

	public int add(int x, int y) {
		// TODO Auto-generated method stub
		return x+y;
	}

	public Object subtract(int x, int y) {
		// TODO Auto-generated method stub
		return x-y;
	}

	public Object Division(int x, int y) {
		// TODO Auto-generated method stub
		return x/y;
	}
	
	

}
